import React, { useEffect, useState } from 'react'
import { Col, Row, Container, Button } from 'react-bootstrap'
import Navigation from '../Components/Navigation'
import axios from 'axios'
import { Link } from 'wouter'

// import { Link } from 'wouter'
// import { Button, Container } from 'react-bootstrap'

const BlogPostscreen = ({ blogposts }) => {
  const [blogpost, setBlogpost] = useState()

  useEffect(() => {
    const fetchBlogpost = async () => {
      const { data } = await axios.get('/api/blogposts/:id')

      setBlogpost(data)
    }
    fetchBlogpost()
  })
  return (
    <div>
      <Navigation />
      <Container>
        <Link href='/Blogscreen'>
          <Button variant='dark'>Back</Button>
        </Link>
        <Row>
          <Col>
            <h1>Testing</h1>
            {blogposts &&
              blogposts.map((blogpost) => (
                <div className='blogposts' key={blogpost}>
                  <Row>
                    <Col>{blogpost.date}</Col>
                    <Col>{blogpost.name}</Col>
                    <Col>
                      <Link href={`/blogposts/${blogpost._id}`}>
                        {blogpost.content.substring(0, 15)}...{' '}
                      </Link>
                    </Col>
                  </Row>
                </div>
              ))}
          </Col>
        </Row>
      </Container>
    </div>
  )
}

export default BlogPostscreen
